package main

import "fmt"

func main() {
	floor := 0
	stop := 0
	for _, c := range input {
		switch c {
		case '(':
			floor += 1
		case ')':
			floor -= 1
		default:
			panic(fmt.Sprintf("don't know what a %q is", c))
		}

		stop += 1

		if floor == -1 {
			break
		}
	}

	fmt.Println("stop", stop)
}

